const accordionItems=document.querySelectorAll(".card__item-header"),accordionContent=document.querySelectorAll(".card__item-content");accordionItems.forEach((c,e)=>{c.addEventListener("click",()=>{c.classList.contains("card__item-active")?(c.classList.remove("card__item-active"),accordionContent[e].classList.add("hidden")):(accordionItems.forEach(c=>{c.classList.remove("card__item-active")}),accordionContent.forEach(c=>{c.classList.add("hidden")}),c.classList.add("card__item-active"),accordionContent[e].classList.remove("hidden"))})});